INSERT INTO `faqs` (`id`, `title`, `description`, `status`, `created_by_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, '{\"en\":\"What is the best way to book a ride?\",\"ar\":\"ما هي أفضل طريقة لحجز رحلة؟\",\"fr\":\"Quelle est la meilleure façon de réserver un trajet ?\",\"de\":\"Was ist der beste Weg, eine Fahrt zu buchen?\"}', '{\"en\":\"You can book a ride easily using our app or website. Simply enter your pickup location, select your vehicle type, and confirm the ride.\",\"ar\":\"يمكنك حجز رحلة بسهولة باستخدام تطبيقنا أو موقعنا الإلكتروني. أدخل موقع الالتقاط، اختر نوع المركبة، وأكد الرحلة.\",\"fr\":\"Vous pouvez facilement réserver un trajet via notre application ou notre site web. Entrez simplement votre lieu de prise en charge, sélectionnez votre type de véhicule et confirmez le trajet.\",\"de\":\"Sie können ganz einfach über unsere App oder Website eine Fahrt buchen. Geben Sie einfach Ihren Abholort ein, wählen Sie die Fahrzeugart aus und bestätigen Sie die Fahrt.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:33:25', NULL),
(2, '{\"en\":\"What payment options are available?\",\"fr\":\"Quelles options de paiement sont disponibles ?\",\"ar\":\"ما هي خيارات الدفع المتاحة؟\",\"de\":\"Welche Zahlungsmöglichkeiten gibt es?\"}', '{\"en\":\"We support multiple payment methods, including credit/debit cards, mobile wallets, and cash, to ensure a convenient experience for you.\",\"ar\":\"ندعم طرق دفع متعددة، بما في ذلك بطاقات الائتمان/الخصم، والمحافظ الإلكترونية، والنقد، لضمان تجربة مريحة لك.\",\"fr\":\"Nous prenons en charge plusieurs méthodes de paiement, y compris les cartes de crédit/débit, les portefeuilles mobiles et les espèces, pour une expérience pratique.\",\"de\":\"Wir unterstützen verschiedene Zahlungsmethoden, darunter Kredit-/Debitkarten, mobile Wallets und Bargeld, um Ihnen eine bequeme Erfahrung zu bieten.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:36:09', NULL),
(3, '{\"en\":\"How can I track my ride?\",\"fr\":\"Comment puis-je suivre mon trajet ?\",\"de\":\"Wie kann ich meine Fahrt verfolgen?\",\"ar\":\"كيف يمكنني تتبع رحلتي؟\"}', '{\"en\":\"Once your ride is confirmed, you can track your driver’s real-time location through the app to know when they’ll arrive.\",\"ar\":\"بمجرد تأكيد رحلتك، يمكنك تتبع موقع السائق في الوقت الفعلي من خلال التطبيق لمعرفة متى سيصل.\",\"fr\":\"Une fois votre trajet confirmé, vous pouvez suivre la position en temps réel de votre chauffeur via application pour savoir quand il arrivera.\",\"de\":\"Sobald Ihre Fahrt bestätigt ist, können Sie den Echtzeitstandort Ihres Fahrers über die App verfolgen, um zu wissen, wann er ankommt.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:38:31', NULL),
(4, '{\"en\":\"Can I cancel a ride after booking?\",\"de\":\"Kann ich eine Fahrt nach der Buchung stornieren?\",\"fr\":\"Puis-je annuler un trajet après la réservation ?\",\"ar\":\"هل يمكنني إلغاء الرحلة بعد الحجز؟\"}', '{\"en\":\"Yes, you can cancel your ride through the app before it starts. Cancellation fees may apply depending on the timing.\",\"de\":\"Ja, Sie können Ihre Fahrt vor Beginn über die App stornieren. Je nach Zeitpunkt können Stornierungsgebühren anfallen.\",\"fr\":\"Oui, vous pouvez annuler votre trajet via l\'application avant qu\'il ne commence. Des frais annulation peuvent appliquer en fonction du moment.\",\"ar\":\"نعم، يمكنك إلغاء رحلتك من خلال التطبيق قبل بدئها. قد تطبق رسوم إلغاء اعتمادًا على التوقيت.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:40:38', NULL),
(5, '{\"en\":\"What is the Rental service?\",\"de\":\"Was ist der Mietservice?\",\"fr\":\"est-ce que le service de location ?\",\"ar\":\"ما هو خدمة التأجير؟\"}', '{\"en\":\"Our rental service allows you to book a vehicle for a set number of hours, ideal for flexible and multiple stops during your trip.\",\"de\":\"Unser Mietservice ermöglicht es Ihnen, ein Fahrzeug für eine bestimmte Anzahl von Stunden zu buchen – ideal für flexible und mehrere Stopps während Ihrer Reise.\",\"fr\":\"Notre service de location vous permet de réserver un véhicule pour un nombre heures déterminé, idéal pour des arrêts flexibles et multiples pendant votre voyage.\",\"ar\":\"تتيح لك خدمة التأجير حجز مركبة لعدد معين من الساعات، مثالية للتوقفات المرنة والمتعددة أثناء رحلتك.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:42:34', NULL),
(6, '{\"en\":\"Can I schedule a ride in advance?\",\"de\":\"Kann ich eine Fahrt im Voraus planen?\",\"fr\":\"Puis-je planifier un trajet à avance ?\",\"ar\":\"هل يمكنني جدولة رحلة مسبقًا؟\"}', '{\"en\":\"Yes, you can schedule a ride for a future date and time directly through our app or website.\",\"de\":\"Ja, Sie können eine Fahrt für ein zukünftiges Datum und eine zukünftige Uhrzeit direkt über unsere App oder Website planen.\",\"fr\":\"Oui, vous pouvez programmer un trajet pour une date et une heure futures directement via notre application ou notre site web.\",\"ar\":\"نعم، يمكنك جدولة رحلة لتاريخ ووقت مستقبلي مباشرة من خلال تطبيقنا أو موقعنا الإلكتروني.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:45:45', NULL),
(7, '{\"en\":\"What should I do if I face an issue during my ride?\",\"de\":\"Was soll ich tun, wenn ich während meiner Fahrt ein Problem habe?\",\"fr\":\"Que dois-je faire si je rencontre un problème pendant mon trajet ?\",\"ar\":\"ماذا أفعل إذا واجهت مشكلة أثناء رحلتي؟\"}', '{\"en\":\"If you encounter any issues, contact our support team through the app or helpline. We’re here to assist you at every step.\",\"de\":\"Wenn Sie auf Probleme stoßen, kontaktieren Sie unser Support-Team über die App oder die Hotline. Wir sind hier, um Ihnen in jeder Phase zu helfen.\",\"fr\":\"En cas de problème, contactez notre équipe de support via application ou la hotline. Nous sommes là pour vous aider à chaque étape.\",\"ar\":\"إذا واجهت أي مشكلات، اتصل بفريق الدعم من خلال التطبيق أو خط المساعدة. نحن هنا لمساعدتك في كل خطوة.\"}', 1, 1, '2025-01-23 00:17:57', '2025-01-23 00:47:56', NULL),
(8, '{\"en\":\"What services do we offer?\",\"de\":\"Welche Dienstleistungen bieten wir an?\",\"fr\":\"Quels services proposons-nous ?\",\"ar\":\"ما هي الخدمات التي نقدمها؟\"}', '{\"en\":\"We offer a variety of services including ride-hailing, rental vehicles, and scheduled rides to suit your needs.\",\"de\":\"Wir bieten eine Vielzahl von Dienstleistungen an, darunter Fahrtenvermittlung, Mietfahrzeuge und geplante Fahrten, die Ihren Bedürfnissen entsprechen.\",\"fr\":\"Nous proposons une gamme de services, notamment le transport à la demande, la location de véhicules et les trajets planifiés pour répondre à vos besoins.\",\"ar\":\"نقدم مجموعة متنوعة من الخدمات بما في ذلك حجز الرحلات، وتأجير المركبات، والرحلات المجدولة لتناسب احتياجاتك.\"}', 1, 1, '2025-01-23 00:49:51', '2025-01-23 00:52:41', NULL);

